  render: () => <div style={{
    background: '#f5f5f5',
    padding: '2rem 0'
  }}>\r
      <Stack spacing="8">\r
        <Container variant="default">\r
          <ContentBox>\r
            <Typography variant="h4" color="white" mb="3">Default Variant (1280px)</Typography>\r
            <Typography color="gray.light">Standard container width suitable for most content layouts and general use cases.</Typography>\r
          </ContentBox>\r
        </Container>\r
        \r
        <Container variant="prose">\r
          <ContentBox>\r
            <Typography variant="h4" color="white" mb="3">Prose Variant (65ch)</Typography>\r
            <Typography color="gray.light">\r
              Optimized for reading text content. The width is set in character units (65ch) which ensures \r
              optimal line length for readability according to typography best practices. Perfect for blog posts, \r
              articles, and documentation.\r
            </Typography>\r
          </ContentBox>\r
        </Container>\r
        \r
        <Container variant="narrow">\r
          <ContentBox>\r
            <Typography variant="h4" color="white" mb="3">Narrow Variant (768px)</Typography>\r
            <Typography color="gray.light">Compact width perfect for forms, modals, and focused content that doesn't need the full width.</Typography>\r
          </ContentBox>\r
        </Container>\r
        \r
        <Container variant="wide">\r
          <ContentBox>\r
            <Typography variant="h4" color="white" mb="3">Wide Variant (1536px)</Typography>\r
            <Typography color="gray.light">Extra wide container ideal for dashboards, data tables, and content that benefits from more horizontal space.</Typography>\r
          </ContentBox>\r
        </Container>\r
        \r
        <Container variant="fluid">\r
          <ContentBox>\r
            <Typography variant="h4" color="white" mb="3">Fluid Variant (100%)</Typography>\r
            <Typography color="gray.light">Full-width container that takes up all available space. Great for backgrounds and full-width layouts.</Typography>\r
          </ContentBox>\r
        </Container>\r
      </Stack>\r
    </div>
